    &__image:first-of-type{
        margin-top: 56px;
    }
    &__image-1{
        background: $darkOverlay, url('../images/portfolio/mobile/image-228b.jpg') center/cover no-repeat;
    }
    &__image-2{
        background: $darkOverlay, url('../images/portfolio/mobile/image-del-sol.jpg') center/cover no-repeat;
    }
    &__image-3{
        background: $darkOverlay, url('../images/portfolio/mobile/image-prototype.jpg') center/cover no-repeat;
    }
    h6{
        color: white; 
        margin-bottom: 11px;
        font-size: 18px;
    }
    p{
        color: white;
        opacity: .8;
    }
    button{
        margin-bottom: 87px;
        width: 100%;
    }
}

@media(min-width: 768px){
    .featured{
        margin: 72px 96px;
        &__image-1{
            background: $darkOverlay, url('../images/portfolio/tablet/image-228b.jpg') center/cover no-repeat;
        }
        &__image-2{
            background: $darkOverlay, url('../images/portfolio/tablet/image-del-sol.jpg') center/cover no-repeat;
        }
        &__image-3{
            background: $darkOverlay, url('../images/portfolio/tablet/image-prototype.jpg') center/cover no-repeat;
        }
        &__image-1::after{
            content: '1';
            @include featureImageAfter;
        }
        &__image-2::after{
            content: '2';
            @include featureImageAfter;
        }
        &__image-3::after{
            content: '3';
            @include featureImageAfter;
        }

    }
}
@media(min-width: 1200px){
    .featured{
        margin: 72px 165px;
        &__image{
            height: 240px;
            margin-bottom: 24px;
            display: flex;
            justify-content: flex-end;
            flex-flow: column;
            padding-left: 24px;
            padding-bottom: 34px;
            position: relative;
            width: 350px;
            height: 560px;
        }
        &__image:first-of-type{
            margin-top: 0px;
        }
        &__image-row{
            display: flex;
            justify-content: space-between;
            margin-top: 85px;
        }
        &__image-1{
            background: $darkOverlay, url('../images/portfolio/desktop/image-228b.jpg') center/cover no-repeat;
        }
        &__image-2{
            background: $darkOverlay, url('../images/portfolio/desktop/image-del-sol.jpg') center/cover no-repeat;
        }
        &__image-3{
            background: $darkOverlay, url('../images/portfolio/desktop/image-prototype.jpg') center/cover no-repeat;
        }
    }
}

